import React, { Component } from 'react';
import EditableCell from "../invoice/editablecell";
import EditableCellSelect from "../invoice/editablecellselect";
class invoicerow extends Component {
    render() {
        let content=this.props.items;
        let options=content.map((data) => 
        <option value={data.name}>{data.name}</option>)
        return (
            <tr className="eachRow">
                    {/*<EditableCellSelect onProductTableUpdate={this.props.onProductTableUpdate}  cellData={{
                        "type":"name",
                        value:this.props.product.name,
                        id:this.props.product.id
                    }} />*/}
                    <select onChange={this.props.onProductTableUpdate} style={{width:'100%'}}>
                        {options}
                    </select>
                    <EditableCell onProductTableUpdate={this.props.onProductTableUpdate} cellData={{
                        "type":"quantity",
                        value:this.props.product.quantity,
                        id:this.props.product.id
                    }} />
                    <EditableCell onProductTableUpdate={this.props.onProductTableUpdate} cellData={{
                        "type":"selling_price",
                        value:this.props.product.selling_price,
                        id:this.props.product.id
                    }} />
                    <EditableCell onProductTableUpdate={this.props.onProductTableUpdate} cellData={{
                        "type":"taxx",
                        value:this.props.product.taxx,
                        id:this.props.product.id
                    }} />
                    <EditableCell onProductTableUpdate={this.props.onProductTableUpdate} cellData={{
                        "type":"amount",
                        value:(this.props.product.selling_price*this.props.product.quantity) + (((this.props.product.quantity)*(this.props.product.selling_price)*(this.props.product.taxx))/100),
                        id:this.props.product.id
                    }} />
            </tr>
        );
    }
}

export default invoicerow;
