import React, { Component } from 'react'
import {Bar} from "react-chartjs-2";
import CustomersForm from '../customers/customersform';
import Customers from '../customers/customers';
import SuppliersForm from '../suppliers/suppliersform';
import Suppliers from '../suppliers/suppliers';
import User from "../user/user";
import {Route,BrowserRouter} from 'react-router-dom';
import InvoiceCreate from '../invoice/invoicecreate';
import Invoices from "../invoice/invoices";
import Products from "../products/products";
import ProductCreate from "../products/productcreate";
import Sale from "../sales/sale";
export default class dashboard extends Component {
    constructor(props)
    {
        super(props);
        this.state={
            chartData:{
                labels:['Aug 2019','Sept 2019','Oct 2019','Nov 2019','Dec 2019','Jan 2020'],
                datasets:[
                    {
                        labels:['Sale','Expense'],
                        data:[
                            47000,
                            75000,
                            55500,
                            67000,
                            87600,
                            50000
                        ],
                        barThickness:30,
                        backgroundColor:[
                            'rgba(54,162,235,.6)',
                            'rgba(255,206,86,.6)',
                            'rgba(54,162,235,.6)',
                            'rgba(255,206,86,.6)',
                            'rgba(54,162,235,.6)',
                            'rgba(255,206,86,.6)',
                        ]
                    }
                ]
            }
        }
    }
    render() {
        return (
            <div className="content-wrapper">
            <div className="content-header">
                <div className="container-fluid">
                    <div className="row mb-2">
                        <div className="col-sm-6">
                            <h1 className="m-0 text-dark">Dashboard</h1>
                        </div>
                        <div className="col-sm-6">
                            <ol className="breadcrumb float-sm-right">
                                <li className="breadcrumb-item">
                                    <a href="#">Home</a>
                                </li>
                            </ol>
                        </div>
                    </div>
                </div>
            </div>
                <div className="container-fluid">
                    {/*<div className="row">
                        <div className="col-lg-3 col-6">
                            <div className="small-box bg-info">
                                <div className="inner">
                                    <h3>150</h3>
                                    <p>New Orders</p>
                                </div>
                                <div className="icon">
                                    <i className="ion ion-bag" />
                                </div>
                                <a href="#" className="small-box-footer">More info <i className="fas fa-arrow-circle-right" /></a>
                            </div>
                        </div>
                        <div className="col-lg-3 col-6">
                            <div className="small-box bg-success">
                                <div className="inner">
                                    <h3>83<sup style={{fontSize: 20}}>%</sup></h3>
                                    <p>Sales</p>
                                </div>
                                <div className="icon">
                                    <i className="ion ion-stats-bars" />
                                </div>
                                <a href="#" className="small-box-footer">More info <i className="fas fa-arrow-circle-right" /></a>
                            </div>
                        </div>
                        <div className="col-lg-3 col-6">
                            <div className="small-box bg-warning">
                                <div className="inner">
                                    <h3 className="text-white">44</h3>
                                    <p className="text-white">New Customers</p>
                                </div>
                                <div className="icon">
                                    <i className="ion ion-person-add" />
                                </div>
                                <a href="#" className="small-box-footer"><span className="text-white">More info</span> <i className="fas fa-arrow-circle-right" style={{color:'white'}}/></a>
                            </div>
                        </div>
                        <div className="col-lg-3 col-6">
                            <div className="small-box bg-danger">
                                <div className="inner">
                                    <h3>30</h3>
                                    <p>Products</p>
                                </div>
                                <div className="icon">
                                    <i className="fas fa-boxes" />
                                </div>
                                <a href="#" className="small-box-footer">More info <i className="fas fa-arrow-circle-right" /></a>
                            </div>
                        </div>
                    </div>*/}
                    <div className="card">
                        <div className="card-body">
                            <Bar
                                data={this.state.chartData}
                                height={75}
                                options={{
                                    legend:{
                                        display:true
                                    },
                                    labels:['Sale','Expense'],
                                    scales:{
                                        xAxis:[{
                                            scaleLabel:{
                                                display:true,
                                                labelString:'Month'
                                            }
                                        }],
                                        yAxis:[{
                                            scaleLabel:{
                                                display:true,
                                                labelString:'Amount'
                                            }
                                        }]
                                    }
                                }}
                            />
                        </div>
                    </div>
                    <Route path={"/customerform"} component={CustomersForm} />
                    <Route path={"/customers"} component={Customers} />
                    <Route path={"/supplierform"} component={SuppliersForm} />
                    <Route path={"/suppliers"} component={Suppliers} />
                    <Route path={"/invoice"} component={Invoices} />
                    <Route path={"/invoicecreate"} component={InvoiceCreate} />
                    <Route path={"/user"} component={User} />
                    <Route path={"/products"} component={Products} />
                    <Route path={"/productcreate"} component={ProductCreate} />
                    <Route path={"/sale"} component={Sale} />
                </div>
        </div>
        )
    }
}
