import React from 'react';
import InvoiceRow from "../invoice/invoicerow";
import "../invoice/invoicenone.css"
import axios from 'axios';
export default class InvoiceCreate extends React.Component {
    constructor(state)
    {
        super(state);
        this.state={
            active:false,
            filterText:"",
            products:[
                {
                    id:'1',
                    product:'',
                    quantity:'',
                    price:'',
                    discount:'',
                    sub_total:'',
                    tax:''
                }
            ],
            items:[
                {
                    id:' ',
                    name:' ',
                    description:' ',
                    hsn_code:' ',
                    sku:' ',
                    tax:[],
                    selling_price:' '
                }
            ],
            customers:[
                {
                    id:' ',
                    first_name:' ',
                    last_name:' ',
                    display_name:' '
                }
            ],
            customer:' ',
            taxes:[
                {
                    tax1:'GST 0',
                    tax2:'GST 5',
                    tax3:'GST 12',
                    tax4:'GST 18',
                    tax5:'GST 28'
                }
            ],
            invoice_no:' ',
            order_no:' ',
            invoice_date:' ',
            payment_terms:' ',
            due_date:' ',
            sold_items:[
                {
                    id:'1',
                    price:' ',
                    quantity:' ',
                    discount_type:' ',
                    discount:' ',
                    sub_total:' ',
                    product:' ',
                    tax:' '
                }
            ],
            pa:[],
            notes:' ',
            terms_and_conditions:' ',
            total_subtotal:' ',
            total_tax:' ',
            total_discount:' ',
            total_amount:' '
        };
    };
    handleUserInput(filterText)
    {
        this.setState({filterText:filterText});
    }
    handleAddEvent = (event) =>
    {
        var len1=this.state.products.length;
        var id=(len1 +1).toString();
        var sold_item={
            id:id,
            product:"",
            quantity:"",
            discount:"",
            discount_type:"",
            price:"",
            tax:"",
            sub_total:""
        }
        this.state.sold_items.push(sold_item);
        this.setState(this.state.sold_items);
    }
    handleProductTable = (event) =>
    {
        event.preventDefault()
        var item={
            id:event.target.id,
            name:event.target.name,
            value:event.target.value
        };
        var sold_items=this.state.sold_items.slice();
        var newProducts=sold_items.map(function(sold_item) {
            for (var key in sold_item) {
                if(key == item.name && sold_item.id == item.id) 
                {
                    sold_item[key]=item.value;
                }
            }
            return sold_item;
        });
        this.setState({sold_items:newProducts})
    };
    updateTax = (event) => {
        event.preventDefault();
        const productnew=[...this.state.sold_items];
        var len=productnew.length;
        if(len == 1)
        {
            productnew[0].tax=(event.target.value).slice(3,6);
            this.setState({sold_items:productnew})
        }
        if(len > 1)
        {
            productnew[len - 1].tax=(event.target.value).slice(3,6);
            this.setState({sold_items:productnew})
        }
        console.log(this.state.sold_items)
    }
    renderSubtotal = () => {
        var len=this.state.sold_items.length;
        var total_subtotal=0;
        for(var i=0;i<len;i++)
        {
            if(len==1)
            {
                total_subtotal=total_subtotal+(this.state.sold_items[i].price)*(this.state.sold_items[i].quantity);
            }
            if(len>1)
            {
                total_subtotal=total_subtotal+(this.state.sold_items[i].price)*(this.state.sold_items[i].quantity);
            }
        }
        return total_subtotal;
    }
    renderTax = () => {
        var len=this.state.sold_items.length;
        var tax=0;
        for(var i=0;i<len;i++)
        {
            if(len==1)
            {
                tax= tax + ((this.state.sold_items[i].price)*(this.state.sold_items[i].tax)*(this.state.sold_items[i].quantity))/100;
            }
            if(len > 1)
            {
                tax=tax + ((this.state.sold_items[i].price)*(this.state.sold_items[i].tax)*(this.state.sold_items[i].quantity))/100;
            }
        }
        return tax;
    }
    renderAmount = () => {
        var len=this.state.sold_items.length;
        var total_amount=0;
        for(var i=0;i<len;i++)
        {
            if(len==1)
            {
                total_amount= total_amount + (this.state.sold_items[i].price)*(this.state.sold_items[i].quantity) + ((this.state.sold_items[i].price)*(this.state.sold_items[i].tax)*(this.state.sold_items[i].quantity))/100;
            }
            if(len > 1)
            {
                total_amount= total_amount + (this.state.sold_items[i].price)*(this.state.sold_items[i].quantity) + ((this.state.sold_items[i].price)*(this.state.sold_items[i].tax)*(this.state.sold_items[i].quantity))/100;
            }
        }
        return total_amount;
    }
    changeHandler =(e) => {
        const {sold_items}= {...this.state};
        const currentState=sold_items;
        const {name,value}=e.target;
        currentState[name]=value;
        this.setState({[e.target.name]: e.target.value ,sold_items:currentState})
    }
    updateHandler = (e) => {
        e.preventDefault();
        const item=[...this.state.sold_items];
        var len=item.length;
        if(len==1)
        {
            item[0].discount_type=e.target.value;
            this.setState({sold_items:item})
        }
        if(len > 1)
        {
            item[len-1].discount_type=e.target.value;
            this.setState({sold_items:item})
        }
        console.log(this.state.sold_items)
    }
    async componentDidMount()
    {
        await axios.get("https://farzi-erp.herokuapp.com/persons_manager/customer/?ordering=created_at")
            .then(res => {
                this.setState({customers:res.data})
                console.log(res)
            })
            .catch(err => {
                console.log(err);
            })
        await axios.get("https://farzi-erp.herokuapp.com/shared/payment_terms/?ordering=created_at")
            .then(res => {
                var t=res.data.length;
                for(var i=0;i<t;i++)
                {
                    var t1=(res.data)[i].id;
                    this.setState({
                        pa:[...this.state.pa,t1]
                    })
                }
                console.log(res);
            })
            .catch(err => {
                console.log(err);
            })
        await axios.get("https://farzi-erp.herokuapp.com/inventory/product/?ordering=created_at")
            .then(res => {
                this.setState({items:res.data})
                console.log(res)
            })
            .catch(err => {
                console.log(err.message)
            })
    }
    render() {
        var someDate = new Date();
        someDate.setDate(someDate.getDate() + 0);
        var date = someDate.toISOString().substr(0, 10);
        var some=new Date();
        some.setDate(some.getDate() + 5);
        var date2=some.toISOString().substr(0,10);
        var onProductTableUpdate=this.handleProductTable;
        var changeHandler=this.changeHandler;
        var items=this.state.items;
        var filterText=this.state.filterText;
        let customerss=this.state.customers;
        let customers=customerss.map((cust) => 
            <option value={cust.id}>{cust.display_name}</option>)
        var prod=this.state.sold_items.map(function(prod) {
            if(prod.product.indexOf(filterText) === -1)
            {
                return ;
            }
            return (<InvoiceRow onProductTableUpdate={onProductTableUpdate} changeHandler={changeHandler} prod={prod} key={prod.id}   items={items} />)
        })
      return (
        <section className="content">
        <div className="row">
            <div className="col-md-12">
                <div className="card card-primary">
                    <div className="card-header">
                        <h3 className="card-title" style={{fontSize:'20px'}}>New Invoice</h3>
                    </div>
                    <form className="form-horizontal">
                        <div className="card-body">
                            <div className="form-group row">
                                <label className="col-sm-2 col-form-label" style={{fontSize:'20px'}}>Customer Name</label>
                                <div className="col-sm-10">
                                    <select className="form-control" onChange={this.changeHandler}  name="customer" >
                                        <option value=" " disabled selected>Choose name</option>
                                        {customers}
                                    </select>
                                </div>
                            </div>
                            <div className="form-group row">
                                <label className="col-sm-2 col-form-label" style={{fontSize:'20px'}}>Invoice No</label>
                                <div className="col-sm-10">
                                    <input type="text" className="form-control" onChange={this.changeHandler} name="invoice_no"></input>
                                </div>
                            </div>
                            <div className="form-group row">
                                <label className="col-sm-2 col-form-label" style={{fontSize:'20px'}}>Order No</label>
                                <div className="col-sm-10">
                                    <input type="text" className="form-control" onChange={this.changeHandler} name="order_no"></input>
                                </div>
                            </div>
                            <div className="form-group row">
                                <label className="col-sm-2 col-form-label" style={{fontSize:'20px'}}>Invoice Date</label>
                                <div className="col-sm-2">
                                    <input type="date" className="form-control" id="today" defaultValue={date} onLoad={this.updateHandler} name="invoice_date" />
                                </div>
                                <label className="col-sm-1 col-form-label" style={{fontSize:'20px'}}>Terms</label>
                                <div className="col-sm-2">
                                    <select className="form-control" onChange={this.changeHandler} name="payment_terms">
                                        <option value={this.state.pa[0]}>Net 15</option>
                                        <option value={this.state.pa[1]}>Net 30</option>
                                        <option value={this.state.pa[2]}>Net 45</option>
                                        <option value={this.state.pa[3]}>Net 60</option>
                                        <option value={this.state.pa[4]}>Due end of month</option>
                                        <option value={this.state.pa[5]}>Due end of next month</option>
                                        <option value={this.state.pa[6]}>Due on receipt</option>
                                    </select>
                                </div>
                                <label className="col-sm-2 col-form-label " style={{fontSize:'20px',marginLeft:'88px'}}>Due Date</label>
                                <div className="col-sm-2">
                                    <input type="date" className="form-control" defaultValue={date2}></input>
                                </div>
                            </div>
                            <table className="table table-bordered">
                                <thead>
                                    <tr>
                                        <th style={{width:'20%'}}><label className="col-form-label" style={{marginLeft:'40px',fontSize:'16px'}}>Item Details</label></th>
                                        <th ><label className="col-form-label" style={{marginLeft:'0px',fontSize:'16px'}}>Quantity</label></th>
                                        <th style={{width:'20%'}}><label className="col-form-label" style={{marginLeft:'35px',fontSize:'16px'}}>Price/Quantity</label></th>
                                        <th style={{width:'20%'}}>
                                            <div className="row">
                                                <div className="col-6">
                                                    <label className="col-form-label" style={{marginTop:'22px',fontSize:'16px'}}>Discount</label>
                                                </div>
                                                <div className="col-6">
                                                    <div className="form-check">
                                                        <input  type="radio" value="amount" name="discount_type" id="amount" style={{marginLeft:'-20px'}} />
                                                        <label  for="amount" style={{marginBottom:'100px'}}>
                                                            Amount
                                                        </label>
                                                    </div>
                                                    <div className="form-check">
                                                        <input className="form-check-input" type="radio" value="percent" name="discount_type" id="percent" />
                                                        <label className="form-check-label" for="percent" style={{marginLeft:'5px'}}>
                                                            Percent
                                                        </label>
                                                    </div>
                                                </div>
                                            </div>
                                        </th>
                                        <th style={{width:'20%'}}><label className="col-form-label" style={{marginLeft:'70px',top:'77%',position:'sticky',fontSize:'16px'}}>Tax</label>
                                            <ul className="navbar-nav" style={{width:'20%'}}>
                                                <li className="nav-item dropdown" style={{width:'20%'}}>
                                                    <a className="nav-link" data-toggle="dropdown" href="#" style={{width:'20%',marginLeft:'30px'}}>
                                                        <i className="fas fa-chevron-down" style={{color:'black',marginLeft:'140px',fontSize:'15px'}}/>
                                                    </a>
                                                    <div className="dropdown-menu dropdown-menu-lg dropdown-menu-left">
                                                        <h6 style={{marginLeft:'-10px'}}>
                                                            <button type="button" className="btn" onClick={this.updateTax} value={this.state.taxes[0].tax1} style={{width:'100%'}}>
                                                                {this.state.taxes[0].tax1}
                                                            </button>
                                                        </h6>
                                                        <div className="dropdown-divider"></div>
                                                        <h6 style={{marginLeft:'-10px'}}>
                                                            <button type="button" className="btn" onClick={this.updateTax} value={this.state.taxes[0].tax2} style={{width:'100%'}}>
                                                                {this.state.taxes[0].tax2}
                                                            </button>
                                                        </h6>
                                                        <div className="dropdown-divider"></div>
                                                        <h6 style={{marginLeft:'-10px'}}>
                                                            <button type="button" className="btn" onClick={this.updateTax} value={this.state.taxes[0].tax3} style={{width:'100%'}}>
                                                                {this.state.taxes[0].tax3}
                                                            </button>
                                                        </h6>
                                                        <div className="dropdown-divider"></div>
                                                        <h6 style={{marginLeft:'-10px'}}>
                                                            <button type="button" className="btn" onClick={this.updateTax} value={this.state.taxes[0].tax4} style={{width:'100%'}}>
                                                                {this.state.taxes[0].tax4}
                                                            </button>
                                                        </h6>
                                                        <div className="dropdown-divider"></div>
                                                        <h6 style={{marginLeft:'-10px'}}>
                                                            <button type="button" className="btn" onClick={this.updateTax} value={this.state.taxes[0].tax5} style={{width:'100%'}}>
                                                                {this.state.taxes[0].tax5}
                                                            </button>
                                                        </h6>
                                                    </div>
                                                </li>
                                            </ul>
                                        </th>
                                        <th style={{width:'20%'}}><label className="col-form-label" style={{marginLeft:'45px',fontSize:'16px'}}>Amount</label></th>
                                    </tr>
                                </thead>
                                <tbody className={this.state.active}>
                                    {prod}
                                </tbody>
                                {console.log(this.state.sold_items)}
                            </table>
                            <button type="button" className="btn btn-outline-success mt-5" onClick={this.handleAddEvent}>Add Another Entry</button>
                            <div className="row">
                                <div className="col-6">
                                    <div className="form-group">
                                        <label for="terms" className="mt-3" style={{fontSize:'20px'}}>Customer Notes</label>
                                        <textarea className="form-control" rows="3" style={{width:'80%'}} onChange={this.changeHandler} name="notes">Thanks for business</textarea>
                                    </div>
                                </div>
                                <div className="col-6">
                                    <label for="terms" style={{fontSize:'20px',marginLeft:'275px'}}>SubTotal:</label>
                                    <label style={{fontSize:'20px',marginLeft:'30px'}}>Rs {this.renderSubtotal()}</label>
                                    <label for="terms" style={{fontSize:'20px',marginLeft:'325px'}}>GST:</label>
                                    <label style={{fontSize:'20px',marginLeft:'30px'}}>Rs {this.renderTax()}</label>
                                    <label for="terms" style={{fontSize:'20px',marginLeft:'277px'}}>Discount:</label>
                                    <label style={{fontSize:'20px',marginLeft:'30px'}}>Rs {this.renderTax()}</label>
                                    {/*<label for="terms" style={{fontSize:'15px',marginLeft:'326px'}}>CGST:</label>
                                    <label style={{fontSize:'18px',marginLeft:'32px'}}>Rs {(this.renderTax())/2}</label>
                                    <label for="terms" style={{fontSize:'15px',marginLeft:'326px'}}>SGST:</label>
<label style={{fontSize:'18px',marginLeft:'32px'}}>Rs {(this.renderTax())/2}</label>*/}
                                    <hr style={{width:'43%',color:'black',marginLeft:'263px'}}/>
                                    <label for="terms" className="mt-2" style={{fontSize:'20px',marginLeft:'285px'}}>Amount:</label>
                                    <label style={{fontSize:'20px',marginLeft:'30px'}}>Rs {this.renderAmount()}</label>
                                </div>
                            </div>
                            <div className="form-group">
                                <label for="terms" className="mt-3" style={{fontSize:'20px'}}>Terms & Conditions</label>
                                <textarea className="form-control" rows="4" style={{width:'50%'}}  onChange={this.changeHandler} name="terms_and_conditions"></textarea>
                            </div>
                        </div>
                        <div className="card-footer">
                            <button type="button" className="btn btn-outline-success">Submit</button>
                        </div>
                    </form>
                </div>
            </div>
        </div>
    </section>
        );

    }

  }
