import React, { PureComponent } from 'react'
import {Link,Route} from 'react-router-dom';
import ProductsDetail from "../products/productsdetail";
import ProductSmall from "../products/productsmall";
import "../invoice/invoicesmall";
class Products extends PureComponent {
    constructor(props) {
        super(props)

        this.state = {
            active:false,
            products:[{no:"1",name:"Television",description:"LED",unit:"Kg",id:"985623",quantity:"1",amount:"50000"},
                      {no:"2",name:"Laptop",description:"Slim",unit:"Kg",id:"985624",quantity:"2",amount:"60000"}],
                      selectedId:'',
                      item:''
        }
        this.toggleClass=this.toggleClass.bind(this);
    }
    cancel = (event) => {
        this.setState({active:false,selectedId:"",item:""})
    }
    setIdHandler = (no) => {
        let itemIndex=this.state.products.findIndex(product => product.no === no);
        let item;
        if(itemIndex !==-1)
        {
            item=this.state.products[itemIndex];
            this.setState({selectedId:no,item,active:true});
        }
        else
        {
            this.setState({selectedId:'',item:'',active:true});
        }
    }
    toggleClass()
    {
        const currentstate=this.state.active;
        this.setState({
            active:!currentstate
        })
    }
    render() {
        return (
            <div>
                <section className="content">
                    <div className='row'>
                        <div className={this.state.active ? 'none' : 'col-12'}>
                            <div className="card">
                                <div className="card-header">
                                    <h3 className="card-title" style={{fontSize:'20px'}}>Items</h3>
                                </div>
                                <div className="card-body">
                                    <table className="table table-bordered" id="productdetails">
                                        <thead>
                                            <tr>
                                                <th style={{fontSize:'15px'}}>No</th>
                                                <th style={{fontSize:'15px'}}>Name</th>
                                                <th style={{fontSize:'15px'}}>Description</th>
                                                <th style={{fontSize:'15px'}}>Unit</th>
                                                <th style={{fontSize:'15px'}}>ID</th>
                                                <th style={{fontSize:'15px'}}>Quantity</th>
                                                <th style={{fontSize:'15px'}}>Amount</th>
                                            </tr>
                                        </thead>
                                        <tbody>
                                            <tr>
                                                <td style={{fontSize:'16px'}}>
                                                    1
                                                </td>
                                                <td style={{fontSize:'16px'}}>
                                                    <button className="btn" onClick={this.toggleClass} style={{marginTop:'-5px'}}>
                                                        <Link to="/products/productsdetails">
                                                            Television
                                                        </Link>
                                                    </button>
                                                </td>
                                                <td style={{fontSize:'16px'}}>
                                                    LED
                                                </td>
                                                <td style={{fontSize:'16px'}}>
                                                    Kg
                                                </td>
                                                <td style={{fontSize:'16px'}}>
                                                    985623
                                                </td>
                                                <td style={{fontSize:'16px'}}>
                                                    1
                                                </td>
                                                <td style={{fontSize:'16px'}}>Rs 50,000</td>
                                            </tr>
                                        </tbody>
                                    </table>
                                </div>
                            </div>
                        </div>
                        <div className={this.state.active ? 'col-4' : 'none'}>
                            <ProductSmall />
                        </div>
                        <div className={this.state.active ? 'col-8' : 'none'}>
                            <Route path={"/products/productsdetails"} component={ProductsDetail} />
                        </div>
                    </div>
                </section>
            </div>

        )
    }
}

export default Products
