import React from 'react';
import InvoiceCreateTable from "../invoice/invoicecreatetable";
export default class InvoiceCreate extends React.Component {
    constructor(props)
    {
        super(props);
        this.state={};
        this.state.filterText="";
        this.state.products=[
            {
                id:1,
                name:'Television',
                quantity:2,
                price:'40000',
                amount:'',
                tax:'5'
            }
        ];
        this.state.taxes=[
            {
                tax1:'5',
                tax2:'10',
                tax3:'12',
                tax4:'18',
                tax5:'28'
            }
        ]
    };
    handleUserInput(filterText)
    {
        this.setState({filterText:filterText});
    }
    handleAddEvent(event)
    {
        var id = (+ new Date() + Math.floor(Math.random() * 999999)).toString(36);
        var product={
            id:id,
            name:"",
            quantity:"",
            price:"",
            tax:"",
            amount:""
        }
        this.state.products.push(product);
        this.setState(this.state.products);
    }
    handleProductTable(event)
    {
        var item={
            id:event.target.id,
            name:event.target.name,
            value:event.target.value
        };
        var products=this.state.products.slice();
        var newProducts=products.map(function(product) {
            for (var key in product) {
                if(key == item.name && product.id == item.id) 
                {
                    product[key]=item.value;
                }
            }
            return product;
        });
        this.setState({products:newProducts});
    };
    render() {
        var someDate = new Date();
        someDate.setDate(someDate.getDate() + 0);
        var date = someDate.toISOString().substr(0, 10);
        var some=new Date();
        some.setDate(some.getDate() + 5);
        var date2=some.toISOString().substr(0,10);
        var onProductTableUpdate=this.props.onProductTableUpdate;
        var filterText=this.props.filterText;
        var product=this.props.products.map(function(product) {
            if(product.name.indexOf(filterText) === -1)
            {
                return ;
            }
            return (<InvoiceRow onProductTableUpdate={onProductTableUpdate} product={product} key={product.id}  />)
        })
      return (
        <div>
            <InvoiceCreateTable onProductTableUpdate={this.handleProductTable.bind(this)} onRowAdd={this.handleAddEvent.bind(this)} products={this.state.products} filterText={this.state.filterText} invoice={this.props.invoice} taxes={this.state.taxes}/>

        </div>
        );

    }

  }
