import React, { Component } from 'react'
import {Bar} from "react-chartjs-2";
import {Link} from 'react-router-dom';
import axios from 'axios';
export default class dashboardcontent extends Component {
    constructor(props)
    {
        super();
        this.state={
            graphdata:{
                monthly:{
                },
                stats:{
                    today:" ",
                    week:" ",
                    month:" ",
                    quarter:" ",
                    year:" "
                }
            },
            chartData:{
                labels:['April 2020','May 2020','June 2020','July 2020','August 2020','September 2020','October 2020','November 2020','December 2020','January 2021','February 2021','March 2021'],
                datasets:[
                    {
                        label:"Sale",
                        data:[
                            47000,
                            75000,
                            55500,
                            67000,
                            87600,
                            50000
                        ],
                        barThickness:30,
                        backgroundColor:'rgba(97,249,85,.8)'
                    },
                    {
                        label:'Expenses',
                        data:[
                            50000,
                            70000,
                            55000,
                            68000,
                            85000,
                            48000
                        ],
                        barThickness:30,
                        backgroundColor:'rgba(250,28,10,.6)'
                    }
                ]
            }
        }
    }
    async componentDidMount() {
        await axios.get("https://farzi-erp.herokuapp.com/invoice/sale_data/")
            .then(res => {
                this.setState({graphdata:res.data})
                console.log(res.data)
            })   
            .catch(err => {
                console.log(err)
            })
    }
    render() {
        return (
            <div>
                    <div className="content-header">
                        <div className="container-fluid">
                            <div className="row mb-2">
                                <div className="col-sm-4">
                                    <h1 className="mt-2 text-dark" style={{fontFamily:'Acme',fontSize:'30px'}}>Dashboard</h1>
                                </div>
                                <div className="col-sm-8">
                                    <h6 className="mt-3" style={{marginLeft:'440px',color:'#868785',fontFamily:'Acme',fontSize:'18px'}}>Helpline - 1800 102 5671</h6>
                                    <h6 style={{fontWeight:'normal',marginLeft:'460px',fontSize:'12px',marginTop:'-5px',color:'#868785',fontFamily:'Acme',fontSize:'13px'}}>Mon-Sat 9:00AM - 7:00PM</h6>
                                    <h6 style={{marginLeft:'660px',marginTop:'-48px',fontFamily:'Acme',fontSize:'20px'}}><Link to="/">Home</Link></h6>
                                </div>
                            </div>
                        </div>
                    </div>
                <div className="card">
                    <div className="card-body">
                        <Bar
                            data={this.state.chartData}
                            height={100}
                            options={{
                                legend:{
                                    display:true,
                                },
                                scales:{
                                    xAxes:[{
                                        scaleLabel:{
                                            display:true,
                                            labelString:'Month'
                                        },
                                        gridLines:{
                                            drawBorder:true
                                        },
                                        ticks:{
                                            fontColor:'#838181'
                                        }
                                    }],
                                    yAxes:[{
                                        scaleLabel:{
                                            display:true,
                                            labelString:'Amount'
                                        },
                                        gridLines:{
                                            drawBorder:true,
                                            display:false
                                        },
                                        ticks:{
                                            fontColor:'#9c9c9c',
                                            stepSize:10000
                                        },
                                    }]
                                }
                            }}
                        />
                    </div>
                </div>
                <div className="row">
                    <div className="col-6">
                        <div className="card">
                            <div className="card-header">
                                <h6 className="card-title" style={{color:'#737673',fontFamily:'Acme',fontSize:'20px'}}>Sales,Expenses and Dues</h6>
                            </div>
                            <div className="card-body">
                                <table className="table table-hover">
                                    <thead>
                                        <tr>
                                            <th></th>
                                            <th style={{fontWeight:'normal',color:'#868785',fontFamily:'Acme'}}>SALES</th>
                                            <th style={{fontWeight:'normal',color:'#868785',fontFamily:'Acme'}}>EXPENSES</th>
                                            <th style={{fontWeight:'normal',color:'#868785',fontFamily:'Acme'}}>DUES</th>
                                        </tr>
                                    </thead>
                                    <tbody style={{color:'#737673'}}>
                                        <tr style={{borderBottom:'0'}}>
                                            <td style={{fontFamily:'Acme',fontSize:'16px'}}>Today</td>
                                            <td style={{fontFamily:'Acme',fontSize:'16px'}}>Rs 5000</td>
                                            <td style={{fontFamily:'Acme',fontSize:'16px'}}>Rs 5500</td>
                                            <td style={{fontFamily:'Acme',fontSize:'16px'}}>Rs 200</td>
                                        </tr>
                                        <tr>
                                            <td style={{fontFamily:'Acme',fontSize:'16px'}}>This Week</td>
                                            <td style={{fontFamily:'Acme',fontSize:'16px'}}>Rs 50000</td>
                                            <td style={{fontFamily:'Acme',fontSize:'16px'}}>Rs 55000</td>
                                            <td style={{fontFamily:'Acme',fontSize:'16px'}}>Rs 20000</td>
                                        </tr>
                                        <tr>
                                            <td style={{fontFamily:'Acme',fontSize:'16px'}}>This Month</td>
                                            <td style={{fontFamily:'Acme',fontSize:'16px'}}>Rs 150000</td>
                                            <td style={{fontFamily:'Acme',fontSize:'16px'}}>Rs 10000</td>
                                            <td style={{fontFamily:'Acme',fontSize:'16px'}}>Rs 60000</td>
                                        </tr>
                                        <tr>
                                            <td style={{fontFamily:'Acme',fontSize:'16px'}}>This Quarter</td>
                                            <td style={{fontFamily:'Acme',fontSize:'16px'}}>Rs 700000</td>
                                            <td style={{fontFamily:'Acme',fontSize:'16px'}}>Rs 650000</td>
                                            <td style={{fontFamily:'Acme',fontSize:'16px'}}>Rs 100000</td>
                                        </tr>
                                        <tr>
                                            <td style={{fontFamily:'Acme',fontSize:'16px'}}>This Year</td>
                                            <td style={{fontFamily:'Acme',fontSize:'16px'}}>Rs 10000000</td>
                                            <td style={{fontFamily:'Acme',fontSize:'16px'}}>Rs 9000000</td>
                                            <td style={{fontFamily:'Acme',fontSize:'16px'}}>Rs 2000000</td>
                                        </tr>
                                    </tbody>
                                </table>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        )
    }
}
