import React from 'react';
import InvoiceCreateTable from "../invoice/invoicecreatetable";
import axios from 'axios';
export default class InvoiceCreate extends React.Component {
    constructor(props)
    {
        super(props);
        this.state={};
        this.state.color='black'
        this.state.filterText="";
        this.state.products=[
            {
                id:'1',
                name:'',
                quantity:'',
                selling_price:'',
                amount:'',
                taxx:''
            },
        ];
        this.state.items=[
            {
                id:' ',
                name:' ',
                description:' ',
                hsn_code:' ',
                sku:' ',
                tax:[],
                quantity:' ',
                selling_price:' '
            }
        ];
        this.state.customers=[
            {
                id:' ',
                first_name:' ',
                last_name:' ',
                display_name:' '
            }
        ]
        this.state.taxes=[
            {
                tax1:'GST 5',
                tax2:'GST 10',
                tax3:'GST 12',
                tax4:'GST 18',
                tax5:'GST 28'
            }
        ]
    };
    handleUserInput(filterText)
    {
        this.setState({filterText:filterText});
    }
    handleAddEvent(event)
    {
        var len1=this.state.products.length;
        /*var id = (+ new Date() + Math.floor(Math.random() * 999999)).toString(36);*/
        var id=(len1 +1).toString();
        var product={
            id:id,
            name:"",
            quantity:"",
            selling_price:"",
            taxx:"",
            amount:""
        }
        this.state.products.push(product);
        this.setState(this.state.products);
    }
    handleProductTable(event)
    {
        event.preventDefault()
        var item={
            id:event.target.id,
            name:event.target.name,
            value:event.target.value
        };
        console.log(item)
        var products=this.state.products.slice();
        var newProducts=products.map(function(product) {
            for (var key in product) {
                if(key == item.name && product.id == item.id) 
                {
                    product[key]=item.value;
                }
            }
            return product;
        });
        this.setState({products:newProducts})
        console.log(this.state.products)
    };
    updateTax = (event) => {
        event.preventDefault();
        const productnew=[...this.state.products];
        var len=productnew.length;
        if(len == 1)
        {
            productnew[0].taxx=(event.target.value).slice(3,6);
            this.setState({products:productnew})
        }
        if(len > 1)
        {
            productnew[len - 1].taxx=(event.target.value).slice(3,6);
            this.setState({products:productnew})
        }
    }
    async componentDidMount()
    {
        await axios.get("https://farzi-erp.herokuapp.com/persons_manager/customer/?ordering=created_at")
            .then(res => {
                this.setState({})
            })
        await axios.get("https://farzi-erp.herokuapp.com/inventory/product/?ordering=created_at")
            .then(res => {
                this.setState({items:res.data})
                console.log(res)
            })
            .catch(err => {
                console.log(err.message)
            })
    }
    render() {
      return (
            <InvoiceCreateTable onProductTableUpdate={this.handleProductTable.bind(this)} onRowAdd={this.handleAddEvent.bind(this)} products={this.state.products} filterText={this.state.filterText}  taxes={this.state.taxes} updateTax={this.updateTax} items={this.state.items} color={this.state.color} />
        );

    }

  }
