import React, { Component } from 'react'
import {Bar} from 'react-chartjs-2';
import axios from 'axios';
export default class user extends Component {
    constructor(props)
    {
        super(props);
        this.state={
            employee:[{
                id:' ',
                first_name:' ',
                last_name:' ',
                display_name:' ',
                email:' ',
                phone:' '
            }]
            }
    }
    componentDidMount()
    {
        axios.get("https://farzi-erp.herokuapp.com/persons_manager/employee/")
            .then(res => {
                this.setState({employee:res.data})
                console.log(res);
            })
            .catch(err => {
                console.log(err);
            })
    }
    render() {
        return (
            <div>
                <section className="content">
                    <div className="row">
                        <div className="col-md-12">
                            <div className="card card-success card-outline">
                                <div className="card-body">
                                    <h4 style={{fontSize:'22px',fontFamily:'Acme'}}>Personal Information</h4>
                                    <div className="row">
                                        <div className="col-4">
                                            <h3 className="profile-username mt-3" style={{fontSize:'20px',fontFamily:'Acme',margintop:'100px'}}>{this.state.employee[0].display_name}</h3>
                                        </div>
                                        <div className="col-4">
                                            <h3 className="mt-3" style={{fontSize:'20px',fontFamily:'Acme'}}>{this.state.employee[0].email}</h3>
                                        </div>
                                        <div className="col-4">
                                            <h3 className="mt-3" style={{fontSize:'20px',fontFamily:'Acme'}}>{this.state.employee[0].mobile}</h3>
                                        </div>
                                    </div>
                                    <h4 style={{fontSize:'22px',fontFamily:'Acme'}}>Address</h4>
                                    <h3 className="profile-username">Billing Address</h3>
                                    <h3 style={{fontSize:'18px'}}>3rd Floor,Mayur Gardens</h3>
                                    <h3 style={{fontSize:'18px'}}>Opp HDFC Bank,G.S Road</h3>
                                    <h3 style={{fontSize:'18px'}}>Guwahati,781005</h3>
                                    <h3 style={{fontSize:'18px'}}>Assam</h3>
                                    <h3 style={{fontSize:'24px'}}>Sales</h3>
                                    <h4 className="text-danger" style={{fontSize:'22px'}}>Rs 52,900</h4>
                                    <h3 style={{marginTop:'-70px',fontSize:'24px',marginLeft:'250px'}}>Unused Credits</h3>
                                    <h4 className="text-danger" style={{marginLeft:'500px',marginTop:'-39px'}}>Rs 0.00</h4>
                                    <h3 style={{fontSize:'24px',marginLeft:'250px'}}>Payment due period</h3>
                                    <h4 className="text-danger" style={{marginLeft:'500px',marginTop:'-37px'}}>Due on Receipt</h4>
                                    <h3 className="text-center mt-5">
                                        Sales
                                    </h3>
                                </div>                               
                            </div>
                        </div>
                    </div>
                </section>
            </div>
        )
    }
}
