import React, { PureComponent } from 'react';
import {Link,Route} from "react-router-dom";
import Invoice from "./invoice";
import InvoiceTable from "./invoicetable";
class Invoices extends PureComponent {
        state = {
            active:false,
            invoices:[{ date:'02/02/2020',no:'00001',orderno:'78549',name:'Mr Suresh Goel',duedate:'15/02/2020',amount:'177120'},
            { date:'04/02/2020',no:'00002',orderno:'78550',name:'Mr Rohit Bansal',duedate:'16/02/2020',amount:'60000'}],
            selectedId:'',
            product:''
        }
    setIdHandler=(no)=>{
        let productIndex=this.state.invoices.findIndex(invoice=>invoice.no===no);
        let product;
        if(productIndex!==-1)
            {
              product=this.state.invoices[productIndex];
              this.setState({selectedId:no,product});
             }
       else
       {
           this.setState({selectedId:'',product:''});
       }
      }
    toggleClass()
    {
        const currentstate=this.state.active;
        this.setState({active:!currentstate})
    }
    render() {
        return (
            <div>
                <section className="content">
                    <div className="row dynamic-columns">
                        <div className={this.state.active ? 'col-7' : 'col-12'}>
                            <div className="card">
                                <div className="card-header">
                                    <h3 className="card-title">All Invoices</h3>
                                </div>
                                <div className="card-body">
                                    <InvoiceTable invoices={this.state.invoices} selectedIdhandler={this.setIdHandler}/>
                                    { this.state.selectedId  ? <Invoice invoice={this.state.product} /> : null}  
                                </div>
                            </div>
                        </div>
                        <div className={this.state.active ? 'col-5' : null}>
                            <Route path={"/invoice/invoicedetails"} component={InvoiceDetails} />
                        </div>
                    </div>
                </section>
            </div>
        )
    }
}

export default Invoices
