import React,{Component} from 'react';
import Table from './Table';
import Invoice from './Invoice';
import invoice from './Invoice';
class invoices extends Component {

     state ={
         invoices:[{ id:'1',name:'Divyanshu',product :'Laptop',price:'45'},
         { id:'2',name:'Prajwal',product :'LED',price:'45'},
         { id:'3',name:'Rachit',product :'TV',price:'35'}],
         selectedId:'',
         product:''
     }

     setIdHandler=(id)=>{
       let productIndex=this.state.invoices.findIndex(invoice=>invoice.id===id);
       let product;
       if(productIndex!==-1)
           {
             product=this.state.invoices[productIndex];
             this.setState({selectedId:id,product});
            }
      else
      {
          this.setState({selectedId:'',product:''});
      }

     }
     render(){
         return (
             <div>
                 <Table invoices={this.state.invoices} selectedIdhandler={this.setIdHandler}/>
                 { this.state.selectedId  ? <Invoice invoice={this.state.product} /> : null}
             </div>

         )
     }
}
export default invoices;
