import React, { Component } from 'react';

class invoicecreatetable extends Component {
    render() {
        var onInvoiceTableUpdate=this.props.onInvoiceTableUpdate;
        var filterText=this.props.filterText;
        var detail=this.props.details.map(function(detail) {
            if(detail.item.indexOf(filterText) === -1)
            {
                return ;
            }
            return (<InvoiceRow onInvoiceTableUpdate={onInvoiceTableUpdate} detail={detail} key={detail.id} />)
        })
        return (
            <div id="invoicecreatetable">
                <table className="table table-bordered" id='datas'>
                    <thead>
                        <tr>
                            <th>Item Details</th>
                            <th>Quantity</th>
                            <th>Price</th>
                            <th>Tax</th>
                            <th>Amount</th>
                        </tr>
                    </thead>
                    <tbody>
                        {detail}
                    </tbody>
                </table>
            </div>
        );
    }
}

export default invoicecreatetable;
