import React, { PureComponent } from 'react';
import {Link,Route} from "react-router-dom";
import InvoiceDetails from "../invoice/invoicedetails";
import InvoiceTable from "../invoice/invoicetable";
class Invoice extends PureComponent {
    constructor(props) {
        super(props)

        this.state = {
            active:false,
            invoices:[{ date:'02/02/2020',no:'00001',orderno:'78549',name:'Mr Suresh Goel',duedate:'15/02/2020',amount:'177120'},
            { id:'2',name:'Prajwal',product :'LED',price:'45'},
            { id:'3',name:'Rachit',product :'TV',price:'35'}],
            selectedId:'',
            product:''
        }
        this.toggleClass=this.toggleClass.bind(this);
    }
    toggleClass()
    {
        const currentstate=this.state.active;
        this.setState({active:!currentstate})
    }
    render() {
        return (
            <div>
                <section className="content">
                    <div className="row dynamic-columns">
                        <div className={this.state.active ? 'col-7' : 'col-12'}>
                            <div className="card">
                                <div className="card-header">
                                    <h3 className="card-title">All Invoices</h3>
                                </div>
                                <div className="card-body">
                                    <InvoiceTable invoices={this.state.invoices} selectedIdhandler={this.setIdHandler}/>
                                    { this.state.selectedId  ? <InvoiceDetails invoice={this.state.product} /> : null}  
                                </div>
                            </div>
                        </div>
                        <div className={this.state.active ? 'col-5' : null}>
                            <Route path={"/invoice/invoicedetails"} component={InvoiceDetails} />
                        </div>
                    </div>
                </section>
            </div>
        )
    }
}

export default Invoice
