import React, { Component } from 'react';
import EditableCell from "../invoice/editablecell";
class invoicerow extends Component {
    render() {
        let content=this.props.items;
        let options=content.map((data) => 
        <option value={data.id}>{data.name + " " + data.description}</option>)
        return (
            <tr className="eachRow">
                <select onChange={this.props.changeHandler} style={{width:'90%',marginTop:'12px',height:'30px',marginLeft:'13px'}} name="product" id={this.props.product.id}>
                    <option value=" " disabled selected>Choose item</option>
                    {options}
                </select>
                <EditableCell onProductTableUpdate={this.props.changeHanlder} cellData={{
                    "type":"quantity",
                    id:this.props.product.id
                }} />
                <EditableCell onProductTableUpdate={this.props.changeHandler} cellData={{
                    "type":"price",
                    id:this.props.product.id
                }} />
                <EditableCell onProductTableUpdate={this.props.changeHandler} cellData={{
                    "type":"discount",
                    id:this.props.product.id
                }} />
                <EditableCell onProductTableUpdate={this.props.changeHandler} cellData={{
                    "type":"tax",
                    id:this.props.product.id
                }} />
                <EditableCell onProductTableUpdate={this.props.changeHandler} cellData={{
                    "type":"amount",
                    id:this.props.product.id
                }} />
            </tr>
        );
    }
}

export default invoicerow;
