import React from 'react';
import Modalform from './modalform';
import InvoiceCreateTable from "../invoice/invoicecreatetable";
export default class InvoiceCreate extends React.Component {
    constructor(props)
    {
        super(props);
        this.state={
            item:'',
            quantity:'',
            price:'',
            tax:'',
            amount:'',
            combined:[],
            active:false
        }
    };
    handleFormSubmit = (e) => {
        e.preventDefault();
        let combined = [...this.state.combined];
        combined.push({item:this.state.item,
                        quantity:this.state.quantity,
                        price:this.state.price,
                        tax:this.state.tax,
                        amount:this.state.amount});
        this.setState({
            combined,
            item:'',
            quantity:'',
            price:'',
            tax:'',
            amount:''
        });
    };
    handleInputChange = (e) => {
        let input=e.target;
        let name=e.target.name;
        let value=input.value;
        console.log(value);
        this.setState({
            item:value
        });
        console.log(this.state);
    };
    openHandle = () => {
        console.log(this.state.active)
        let update={item:<input type="text" value={this.state.item} onChange={this.handleInputChange} style={{width:'90%'}}></input>,quantity:<input type="text" value="" style={{width:'90%'}}></input>,price:<input type="text" value="" style={{width:'90%'}}></input>,tax:<input type="text" value="" style={{width:'90%'}}></input>,amount:<input type="text" value="" style={{width:'90%'}}></input>};
        let combined = [...this.state.combined];
        combined.push(update);
        this.setState({
            combined:combined

        })
    } 
    render() {
      return (
        <div>
            <section className="content">
                    <div className="row">
                        <div className="col-md-12">
                            <div className="card card-primary">
                                <div className="card-header">
                                    <h3 className="card-title" style={{fontSize:'20px'}}>New Invoice</h3>
                                </div>
                                <form className="form-horizontal">
                                    <div className="card-body">
                                        <div className="form-group row">
                                            <label className="col-sm-2 col-form-label" style={{fontSize:'20px'}}>Customer Name</label>
                                            <div className="col-sm-10">
                                                <input type="text" className="form-control"></input>
                                            </div>
                                        </div>
                                        <div className="form-group row">
                                            <label className="col-sm-2 col-form-label" style={{fontSize:'20px'}}>Invoice No</label>
                                            <div className="col-sm-10">
                                                <input type="text" className="form-control"></input>
                                            </div>
                                        </div>
                                        <div className="form-group row">
                                            <label className="col-sm-2 col-form-label" style={{fontSize:'20px'}}>Order No</label>
                                            <div className="col-sm-10">
                                                <input type="text" className="form-control"></input>
                                            </div>
                                        </div>
                                        <div className="form-group row">
                                            <label className="col-sm-2 col-form-label" style={{fontSize:'20px'}}>Invoice Date</label>
                                            <div className="col-sm-2">
                                                <input type="date" className="form-control"></input>
                                            </div>
                                            <label className="col-sm-2 col-form-label" style={{fontSize:'20px'}}>Terms</label>
                                            <div className="col-sm-2">
                                                <select className="form-control">
                                                    <option>Due On Receipt</option>
                                                    <option>Due end of month</option>
                                                    <option>Due end of next month</option>
                                                </select>
                                            </div>
                                            <label className="col-sm-2 col-form-label " style={{fontSize:'20px'}}>Due Date</label>
                                            <div className="col-sm-2">
                                                <input type="date" className="form-control"></input>
                                            </div>
                                        </div>
                                        <div className="form-group row">
                                            <label className="col-sm-2 col-form-label" style={{fontSize:'20px'}}>Salesperson</label>
                                            <div className="col-sm-10">
                                                <input type="text" className="form-control"></input>
                                            </div>
                                        </div>
                                        <InvoiceCreateTable combined={this.state.combined}/>
                                    </div>
                                    <div className="card-footer">
                                        <button type="button" className="btn btn-outline-success"  onClick={this.openHandle}>Add Another Entry</button>
                                    </div>
                                    {/*<Modalform handleFormSubmit={this.handleFormSubmit} handleInputChange={this.handleInputChange} newItem={this.state.item} newQuantity={this.state.quantity} newPrice={this.state.price} newTax={this.state.tax} newAmount={this.state.amount} />*/} 
                                </form>
                            </div>
                        </div>
                    </div>
                </section>
                <Modalform />
            </div>
        );

    }

  }
