import React, { PureComponent } from 'react';
import {Link,Route} from "react-router-dom";
import InvoiceDetails from "../invoice/invoicedetails";
import InvoiceTable from "../invoice/invoicetable";
import InvoiceSmall from "../invoice/invoicesmall";
import "./invoicenone.css"
class Invoice extends PureComponent {
    constructor(props)
    {
        super(props);
        this.state={
            active:false,
            invoices:[{ date:'02/02/2020',no:'00001',orderno:'78549',name:'Mr Suresh Goel',duedate:'15/02/2020',amount:'177120'},
                    { date:'04/02/2020',no:'00002',orderno:'78550',name:'Mr Rohit Bansal',duedate:'16/02/2020',amount:'60000'},
                    { date:'02/02/2020',no:'00001',orderno:'78549',name:'Mr Suresh Goel',duedate:'15/02/2020',amount:'177120'}]
        }
    }
    toggleClass = (event) => 
    {
        const currentstate=this.state.active;
        this.setState({active:!currentstate})
    }
    setIdHandler=(id)=>{
        let productIndex=this.state.invoices.findIndex(invoice=>invoice.id===id);
        let product;
        if(productIndex!==-1)
            {
              product=this.state.invoices[productIndex];
              this.setState({selectedId:id,product});
             }
       else
       {
           this.setState({selectedId:'',product:''});
       }

      }
    render() {
        return (
            <div>
                <section className="content">
                    <div className="row dynamic-columns">
                        <div className={this.state.active ? 'none' : 'col-12'}>
                            <div className="card">
                                <div className="card-header">
                                    <h3 className="card-title">All Invoices</h3>
                                </div>
                                <div className="card-body">
                                    <InvoiceTable  invoices={this.state.invoices}/>
                                </div>
                            </div>
                        </div>
                        <div className={this.state.active ? 'col-4' : 'none'}>
                            <InvoiceSmall />
                        </div>
                        <div className={this.state.active ? 'col-8' : 'none'}>
                             {console.log(this.state.active)}
                            <Route path={"/invoice/invoicedetails"} component={InvoiceDetails} />
                        </div>
                    </div>
                </section>
            </div>
        )
    }
}

export default Invoice
